field.grid_options:
formatters: {
    "status": function (column, row) {
        // For some reason this needs to be a string return, and not a DOM object.
        let span = "<span class='label-default label label-"
        span += row[column['id']]['label']
        span += "'>"
        span += row[column['id']]['text']
        span += "</span>"

        return span;
        }
}

formatters: {
    "boolean": function (column, row) {
        // For some reason this needs to be a string return, and not a DOM object.
        let span = "<span class='label-default label label-"
        span += row[column['id']]['label']
        span += "'>"
        span += "LOL"
        span += "</span>"

        return span;
        },
    "status": function (column, row) {
        // For some reason this needs to be a string return, and not a DOM object.
        let span = "<span class='label-default label label-"
        span += row[column['id']]['label']
        span += "'>"
        span += row[column['id']]['text']
        span += "</span>"

        return span;
        }
}
